import React, { Component, useEffect, useState } from "react";
import { Link, useParams, useLocation } from "react-router-dom";
import "../style/Course.css";
import "../App.css";
import { FormGroup, Input, Button, Jumbotron } from "reactstrap";
import Compiler from './Compiler';
import axios from "axios";
import api from '../api/api.js'

//test
//import practiceQnList from "./test-dataPracticeQn"; 
//import axios from "axios";

// import { Container, Form }from 'react-bootstrap'
// import 'bootstrap/dist/css/bootstrap.min.css'

function ViewPracticeQn(props) {

  const [count, setCounter] = useState(0);
  const [practiceQn, setPracticeQn] = useState(''); 
  
  function useQuery() {
    return new URLSearchParams(useLocation().search);
  }
  const {id} = useParams();
  let query = useQuery();
  

  const [qid, setQid] = useState(id);

  useEffect(() => {
    if(practiceQn === '') {
      getList();
    }
  })

  const getList = async () => {
    const response = await axios.get(`${api}/practice/question/${id}`)
    setPracticeQn(response)
  }
    const openbtn = {
      border: "2px solid white",
      width: "13%",
      display: "flex",
      justifyContent: "center",
      alignItems: "center"
    }

    return (
      <>
        <br />
        <div className="firsthead">
          <h1 style={{ color: "darkslategray", display: "inline-block" }}>
            Problem Statement{" "}
          </h1>
          <button value="Back" id="rd_btn" >
            <Link to={`/practiceques`} style={{ color: "darkslategray" }}>
              {"Back"}
            </Link>{" "} 
          </button> 
        </div> <br/>
        <div className="practiceques">
          <center><b><h2>{practiceQn.Q_Title}</h2></b></center><br/>         
          <i>Type: {practiceQn.Q_Type}</i><br/><br/>
          <i>Question ID: {practiceQn.Q_ID}</i><br/><br/>
          <b>Problem Statement</b><br/><br/>
          <p>
            {
              practiceQn.Q_Question
            }
          </p>
          <b>TestCase</b><br/><br/>
          <p>
            {
              practiceQnList[0].Q_Testcase
            }
          </p>
          
          <b>TestCase Answer</b><br/><br/>
          <p>
            {
              practiceQnList[0].Q_TestcaseAns
            }
          </p>
        </div>
        <div className="questions">
        <div className="entercode">
      
            <label>Programming Language:</label> <br/>
              <select 
              type="select"
                id="type"
                name="type"
                onChange="{this.handleOnChange}"
                 >
                <option value=""></option>
                <option value="C"> C </option>
                <option value="C++"> C++ </option>
                <option value="Java"> Java </option>
                <option value="Python"> Python </option>
              </select>{" "}
            <br /> <br/>

            <label forName=""> Code Here : </label> <br />
            <textarea
              id="codeHere"
              name="w3review1"
              rows="20"
              cols="100"
              placeholder="Code Here"
              defaultValue=""
              ></textarea> <br /> <br />
            <label forName=""> Inputs  : </label> <br />
            <textarea
              id="inputs"
              name="w3review2"
              rows="7"
              cols="100"
              placeholder="Enter Input of Your Code"
              defaultValue=""
            ></textarea>{" "}
            <br /><br />
            <label forName=""> Your Output : </label> <br />
            <textarea
              id="outputCode"
              name="w3review3"
              rows="7"
              cols="100"
              placeholder="Your Output will display here"
              defaultValue=""
            ></textarea> <br/><br/>
            <label forName=""> Expected Result : </label> <br />
            <textarea
              id="expectedResult"
              name="w3review3"
              rows="7"
              cols="100"
              placeholder="Expected Result"
              defaultValue=""
            ></textarea> <br/><br/>
              
            <button style={{width:"100%"}} id="sub-btn" onClick="" >  Run </button> <br/><br/>
            <button style={{width:"100%"}} id="sub-btn" onClick="" >  Submit </button>

            <br/><br/>
          </div>
      

        </div>
      </>
    );
  }

export default ViewPracticeQn;
