import React, { Component, useState } from "react";
import { Link, useParams, useLocation } from "react-router-dom";
import "../style/Course.css";
import "../App.css";

//test
import contestList from "./test-data"; 
import contestQnList from "./test-dataQn"; 

// import { Container, Form }from 'react-bootstrap'
// import 'bootstrap/dist/css/bootstrap.min.css'

function ViewContest(props) {

  const [count, setCounter] = useState(0);
  
  
  function useQuery() {
    return new URLSearchParams(useLocation().search);
  }
  const {id} = useParams();
  let query = useQuery();
  

  const [cid, setCid] = useState(id);

    const openbtn = {
      border: "2px solid white",
      width: "13%",
      display: "flex",
      justifyContent: "center",
      alignItems: "center"
    }

    return (
      <>
        <br />
        <div className="firsthead">
          <h1 style={{ color: "darkslategray", display: "inline-block" }}>
            Contest{" "}
          </h1>
          <button value="Add Questions" id="rd_btn" >
            <Link to="/createContest" style={{ color: "darkslategray" }}>
              {"Add Questions"}
            </Link>{" "} 
          </button> 
        </div> <br/>
        <div className="practiceques">
          <center><b><h2>{contestList[0].ContestTitle}</h2></b></center><br/>         
          <i>STATUS: {contestList[0].ContestStatus}</i><br/><br/>
          <b>ABOUT CONTEST</b><br/><br/>
          <p>
            {
              contestList[0].ContestAbout
            }
          </p>
        </div>

        <div className="practiceques">
        
        {
        contestQnList.map(contest => {
              return(
           
        <div className="Ques-box" style={{display:"flex"}}>
          <div className="Questions" > 
            <div><p>{contest.Q_ContestID}</p></div>
            <div><p>{contest.Q_ID}</p></div>
            <div><p>{contest.Q_Title}</p></div>
            <div><p>{contest.Q_Type}</p></div>
            <div><p>{contest.Q_Title}</p></div>
          </div>
        
          <Link to={`/contest/ViewContest`} style={openbtn} >Open</Link>
        </div>
           )
          })
        }

        </div>
      </>
    );
  }

export default ViewContest;
