import React, { Component, useState } from "react";
import { Link, useParams, useLocation } from "react-router-dom";
import "../style/Course.css";
import "../App.css";
import { FormGroup, Input, Button, Jumbotron } from "reactstrap";
import Compiler from './Compiler';

//test
import contestQnList from "./test-dataQn"; 

// import { Container, Form }from 'react-bootstrap'
// import 'bootstrap/dist/css/bootstrap.min.css'

function ViewContest(props) {

  const [count, setCounter] = useState(0);
  
  
  function useQuery() {
    return new URLSearchParams(useLocation().search);
  }
  const {id} = useParams();
  let query = useQuery();
  

  const [qid, setQid] = useState(id);

    const openbtn = {
      border: "2px solid white",
      width: "13%",
      display: "flex",
      justifyContent: "center",
      alignItems: "center"
    }

    return (
      <>
        <br />
        <div className="firsthead">
          <h1 style={{ color: "darkslategray", display: "inline-block" }}>
            Contest{" "}
          </h1>
          <button value="Back" id="rd_btn" >
            <Link to={`/contest/ViewContest/${contestQnList[0].Q_ContestID}`} style={{ color: "darkslategray" }}>
              {"Back"}
            </Link>{" "} 
          </button> 
        </div> <br/>
        <div className="practiceques">
          <center><b><h2>{contestQnList[0].Q_Title}</h2></b></center><br/>         
          <i>Type: {contestQnList[0].Q_Type}</i><br/><br/>
          <i>Question ID: {contestQnList[0].Q_ID}</i><br/><br/>
          <b>Problem Statement</b><br/><br/>
          <p>
            {
              contestQnList[0].Q_Question
            }
          </p>
          <b>TestCase</b><br/><br/>
          <p>
            {
              contestQnList[0].Q_Testcase
            }
          </p>
          
          <b>TestCase Answer</b><br/><br/>
          <p>
            {
              contestQnList[0].Q_TestcaseAns
            }
          </p>
        </div>
        <div className="questions">
        <div>
            <FormGroup>
                <label>Select Programming Language</label>
                <br/>
                <Input type="select" id="language">
                    <option>c</option>
                    <option>cpp</option>
                    <option>java</option>
                    <option>py</option>
                </Input>
            </FormGroup>
           
            <FormGroup>
                <label>Enter FileName</label>
                <Input type="text" id="filename" />
            </FormGroup>
            
            <FormGroup>
                <label>Code</label>
                <Input type="textarea" 
                    style={{ 
                        height: 250,
                        backgroundColor: "#066f90",
                        color: "#FFFFFF"
                    }} 
                    id="inputCode" />
            </FormGroup>
            <br />
            <br />
            
            <FormGroup>
                <label>Input</label>
                <Input type="textarea"  
                    style={{ 
                        height: 150,
                        backgroundColor: "#066f90",
                        color: "#FFFFFF"
                    }} 
                    id="inputInput" />
            </FormGroup>
            <Button onClick="" color="primary">Download</Button>
            <br />
            <br />
            
        </div>
       </div>
      </>
    );
  }

export default ViewContest;
