import React, { Component } from 'react'
import { Button, Input, Jumbotron} from 'reactstrap'
import AuthenticationService from '../Service/AuthenticationService';
import { Link } from 'react-router-dom';

class Signup extends Component {

    constructor(props) {
        super(props)

        this.state = {
            username: '',
            email: '',
            password: '',
            usernameError: '',
            emailError: '',
            passwordError: '',
            hasLoginFailed: false,
            showSuccessMessage: false
        }

        this.handleChange = this.handleChange.bind(this)
        this.signupClicked = this.signupClicked.bind(this)
    }

    handleChange(event) {
        this.setState(
            {
                [event.target.name]: event.target.value
            }
        )
    }

    validate = () => {
        let usernameError= "";
        let emailError = "";
        let passwordError = "";

        if (!this.state.username) {
            usernameError= 'Username cannot be empty'
        }
        if (!this.state.email.includes('@')) {
            emailError = 'invalid email';
        }

        if (!this.state.password) {
            passwordError = 'Password cannot be empty';
        }
        if(emailError) {
            this.setState({emailError})
            return false;
        }

        if (emailError || usernameError || passwordError) {
            this.setState({ emailError, usernameError, passwordError });
            return false;
        }

        return true;
    }

    signupClicked() {
        const isValid = this.validate();
        if (!isValid) {
            return false;    
        }
        AuthenticationService
        .userRegistrationService(this.state.email, this.state.username, this.state.password)
        .then(() => {
            this.setState({ showSuccessMessage: true })
            this.setState({ hasLoginFailed: false })
        })
        .catch(() => {
            this.setState({ showSuccessMessage: false })
            this.setState({ hasLoginFailed: true })
        })
    }

    render() {

        return(
            <div
            style={{color: 'white'}}
            >  

            <Jumbotron>
                <h1 className="text-center">Sign Up</h1>
                <hr className="my-2" />
                <p className="lead text-center">
                <br />
                <Link className="btn btn-primary" to="/login">Login Here</Link>
                </p>
            </Jumbotron>
            <div className="container">
                {this.state.hasLoginFailed && <div className="alert alert-danger">Email already registered!</div>}
                {this.state.showSuccessMessage && <div className="alert alert-success">Sucessfully Registered</div>}
                <center>
                <form style={{
                    height: '520px',
                    width: '400px',
                    backgroundColor: "rgba(255,255,255,0.13)",
                    position: 'absolute',
                    transform: 'translate(-50%,-50%)',
                    top: '50%',
                    left: '50%',
                    borderRadius: '10px',
                    backdropFilter: 'blur(10px)',
                    border: '2px solid rgba(255,255,255,0.1)',
                    boxShadow: '0 0 40px rgba(8,7,16,0.6)',
                    padding: '50px 35px',
                    fontFamily: 'Poppins, sans-serif',
                    color: '#ffffff',
                    letterSpacing: '0.5px',
                    outline: 'none',
                    border: 'none'
                }}>
                <label style={{
                    fontSize: '22px',
                    fontWeight: '500',
                    lineHeight: '42px',
                    textAlign: 'center',
                    }}>Username:</label> 
                <Input 
                    type="text" 
                    name="username" 
                    value={this.state.username} 
                    onChange={this.handleChange} 
                />
                <div style={{ fontSize: 12, color: "red" }}> {this.state.usernameError} </div>
                <br />
                <label style={{color: 'white'}}>Email: </label>
                <Input 
                    type="text" 
                    name="email" 
                    value={this.state.email} 
                    onChange={this.handleChange} 
                />
                <div style={{ fontSize: 12, color: "red" }}> {this.state.emailError} </div>

                <br />
                <label style={{color: 'white'}}>Password: </label>
                <Input 
                    type="password" 
                    name="password" 
                    value={this.state.password} 
                    onChange={this.handleChange} 
                />
                <div style={{ fontSize: 12, color: "red" }}> {this.state.passwordError} </div>

                <br />
                <Button className="btn btn-success" onClick={this.signupClicked}>Signup</Button>
                </form>
                </center>
            </div>
            <br />
            <br />
        </div>
        )
    }
}

export default Signup
