import React, { Component } from "react";
import { Link } from "react-router-dom";
import "../style/Course.css";
import "../App.css";
import AuthService from "../Service/AuthenticationService"
import axios from "axios";
import api from '../api/api.js'

// import { Container, Form }from 'react-bootstrap'
// import 'bootstrap/dist/css/bootstrap.min.css'



export default class Createques extends Component {

  constructor(props){
    super(props);
    this.state = {
      email : AuthService.getLoggedInUserName(),
      title : "",
      type  : "",
      hints : "",
      relatedTopic : "",
      question : null,
      testcase : null,
      testcaseAns : null,
    };

    this.handleOnchange = this.handleOnchange.bind(this);
    this.onSubmit = this.onSubmit(this);
    this.onAttach = this.onAttach(this);
  }

  handleOnChange(event){
    this.setState(
      {
        [event.target.name]: event.target.value
      }
    )
    event.preventDefault();
  }
  
  onAttach(event){
    const questionFile = new Blob([document.getElementById('inputQn').value],    
    {type: 'text/plain;charset=utf-8'});
    const testcaseFile = new Blob([document.getElementById('inputTC').value],    
    {type: 'text/plain;charset=utf-8'});
    const testcaseAnsFile = new Blob([document.getElementById('inputTCAns').value],    
    {type: 'text/plain;charset=utf-8'});
  
    this.setState({question : questionFile})
    this.setState({testcase : testcaseFile})
    this.setState({testcaseAns : testcaseAnsFile})
  }

  onSubmit(event){
    const formData = new FormData()
    formData.append('email', this.state.email)
    formData.append('title', this.state.title)
    formData.append('type', this.state.type)
    formData.append('hints', this.state.hints)
    formData.append('relatedTopic', this.state.relatedTopic)
    formData.append('question', this.state.question)
    formData.append('testcase', this.state.testcase)
    formData.append('testcaseAns', this.state.testcaseAns)

    return axios.post(`${api}/practice/createQn`)

  }

  render() {
    return (
      <>
        <br />
        <br /> <br />
        <div className="questions">
          <div className="editdetails">
            <form>
              <label forName="">Email:</label>
              <br />
              <input 
                type="email" 
                id="email" 
                forName="fname"
                value={this.state.email} 
                onChange={this.handleOnChange}
                defaultValue={this.state.Email}  /> <br/>
              <br />

              <label forName="">Title:</label>
              <br />
              <input 
                type="text" 
                id="lname"
                value={this.state.title} 
                onChange={this.handleOnChange} 
                forName="" /> 
                <br /> <br/>

              <label forName="">Type:</label>
              <select 
                id="level"
                value={this.state.type} 
                onChange={this.handleOnChange}
                 >
                <option value="Easy"> Easy </option>
                <option value="Medium"> Medium </option>
                <option value="Hard">Hard</option>
              </select>{" "}
              <br /> <br/>

              <label forName="">Hint:</label>
              <br />
              <input 
                type="text" 
                id="lname" 
                value={this.state.hints} 
                onChange={this.handleOnChange}
                forName="" /> <br /> <br/>

              <label forName="">Related Topics:</label>
              <br />
              <input 
                type="text" 
                id="lname" 
                value={this.state.relatedTopic} 
                onChange={this.handleOnChange}
                forName="" /> <br /> <br /> <br/> 
              {/* <input type="submit" value="Submit" id="sub-btn"/>   <br/> */}
              
              <Link to='/practiceques'><button style={{width:"100%"}} id="sub-btn" onClick={this.onSubmit} value="Submit">  Submit </button></Link>
              </form>
          </div>
          <div className="enterquestion">
            <label forName=""> Enter Question : </label> <br />
            <textarea
              id="inputQn"
              name="w3review"
              rows="20"
              cols="70"
              placeholder="Enter Questions Here"
              Value={this.state.ques}
            ></textarea> <br />
            <label forName=""> Enter Test Cases  : </label> <br />
            <textarea
              id="inputTC"
              name="w3review"
              rows="7"
              cols="70"
              placeholder="Enter Test Cases Here"
            ></textarea>{" "}
            <br />
            <label forName=""> Test Cases Answer : </label> <br />
            <textarea
              id="inputTCAns"
              name="w3review"
              rows="7"
              cols="70"
              placeholder="Enter Test Cases Answer Here"
            ></textarea> <br/><br/>
            <button style={{width:"30%"}} id="sub-btn" onClick={this.onAttach} >  Attach </button>
            <br/><br/>
          </div>
        </div>
      </>
    );
  }
}
